package io.github.gcdd1993.chapter4;

/**
 * 二叉查找树 ADT
 * <p>
 * Created by gcdd1993 on 2021/3/15.
 */
public class BinarySearchTree<T extends Comparable<? super T>> {

    private BinaryTreeNode<T> root;

    public BinarySearchTree() {
        root = null;
    }

    public void makeEmpty() {
        root = null;
    }

    public boolean isEmpty() {
        return root == null;
    }

    /**
     * 是否存在此元素
     *
     * @param t 元素
     * @return if find
     */
    public boolean contains(T t) {
        return contains(t, root);
    }

    public T findMin() {
        if (isEmpty()) return null;
        return findMin(root).element;
    }

    public T findMax() {
        if (isEmpty()) return null;
        return findMax(root).element;
    }

    // 需要处理重复元
    public void insert(T x) {
        root = insert(x, root);
    }

    // 删除通常来说，是最困难的
    public void remove(T x) {
        root = remove(x, root);
    }

    public void printTree() {

    }

    /**
     * 在子树中查找元素X
     *
     * @param x 要查找的元素X
     * @param t 子树的根节点
     * @return 如果能找到
     */
    private boolean contains(T x, BinaryTreeNode<T> t) {
        // 如果t是空集，表明已经查找到叶子节点，仍未找到，返回false
        if (t == null) {
            return false;
        }
        // 直接与子树的根节点比较，由于是二叉查找树，与根节点比较，将得出在左子树还是右子树。
        int compareResult = x.compareTo(t.element);
        // 比根节点小，说明在左子树
        if (compareResult < 0) {
            // 递归操作，空间复杂度为O(logN)
            return contains(x, t.left);
        } else
            // 比根节点大，说明在右子树
            if (compareResult > 0) {
                return contains(x, t.right);
            } else {
                // 与根节点相同，说明已经找到
                return true;
            }
    }

    /**
     * 从根节点开始，只要有左子树，就向下遍历，终止点就是最小的元素
     * <p>
     * 递归方式
     *
     * @param t 子树根节点
     * @return min element
     */
    private BinaryTreeNode<T> findMin(BinaryTreeNode<T> t) {
        if (t == null) {
            return null;
        } else if (t.left == null) {
            return t;
        }
        return findMin(t.left);
    }

    /**
     * 从根节点开始，只要有右子树，就向下遍历，终止点就是最大的元素
     * <p>
     * 非递归
     *
     * @param t 子树根节点
     * @return max element
     */
    private BinaryTreeNode<T> findMax(BinaryTreeNode<T> t) {
        if (t != null) {
            while (t.right != null) {
                t = t.right;
            }
        }
        return t;
    }

    /**
     * 在子树中查找元素X
     * <p>
     * 沿着树查找，如果找到X，什么都不用做，或者更新element
     * 否则，将X插入到遍历的路径上的最后一点上
     *
     * <img src="data:image/png;base64,">
     *
     * @param x 要查找的元素X
     * @param t 子树的根节点
     */
    private BinaryTreeNode<T> insert(T x, BinaryTreeNode<T> t) {
        if (t == null) {
            return new BinaryTreeNode<>(x);
        }
        // 直接与子树的根节点比较，由于是二叉查找树，与根节点比较，将得出在左子树还是右子树。
        int compareResult = x.compareTo(t.element);
        // 比根节点小，说明在左子树
        if (compareResult < 0) {
            // 插入X到此节点
            t.left = insert(x, t.left);
        } else
            // 比根节点大，说明在右子树
            if (compareResult > 0) {
                t.right = insert(x, t.right);
            } else {
                // 与根节点相同，说明已经找到，修改节点元素（书中示例为不做任何操作）
                t.element = x;
            }
        return t;
    }

    /**
     * 删除节点
     *
     * <img src="data:image/png;base64,"/>
     *
     * @param x 待删除的节点x
     * @param t 节点
     * @return 被删除的节点
     */
    private BinaryTreeNode<T> remove(T x, BinaryTreeNode<T> t) {
        if (t == null) {
            return null; // 没找到，不删除
        }
        // 直接与子树的根节点比较，由于是二叉查找树，与根节点比较，将得出在左子树还是右子树。
        int compareResult = x.compareTo(t.element);
        // 比根节点小，说明在左子树
        if (compareResult < 0) {
            // 插入X到此节点
            t.left = remove(x, t.left);
        } else if (compareResult > 0) {// 比根节点大，说明在右子树
            t.right = remove(x, t.right);
        } else if (t.left != null && t.right != null) {// 待删除的节点，存在左右子树
            // 用其右子树的最小的数据代替该节点的数据，并递归地删除那个节点(现在它是空的)
            t.element = findMin(t.right).element;
            t.right = remove(t.element, t.right);
        } else {
            // 待删除的节点，只有左子树，或没有子树
            t = (t.left != null) ? t.left : t.right;
        }
        return t;
    }

    /**
     * 二叉查找树节点
     */
    private static class BinaryTreeNode<T> {

        // 需要实现Comparable接口
        private T element;
        private BinaryTreeNode<T> left;
        private BinaryTreeNode<T> right;

        public BinaryTreeNode(T element, BinaryTreeNode<T> left, BinaryTreeNode<T> right) {
            this.element = element;
            this.left = left;
            this.right = right;
        }

        public BinaryTreeNode(T element) {
            this.element = element;
        }

        public Object getElement() {
            return element;
        }

        public void setElement(T element) {
            this.element = element;
        }

        public BinaryTreeNode<T> getLeft() {
            return left;
        }

        public void setLeft(BinaryTreeNode<T> left) {
            this.left = left;
        }

        public BinaryTreeNode<T> getRight() {
            return right;
        }

        public void setRight(BinaryTreeNode<T> right) {
            this.right = right;
        }
    }
}
