package io.github.gcdd1993.chapter4.avltree;

/**
 * AVL树（带有平衡条件的二叉查找树），每个节点左右子树的深度差不超过1
 * 由于每次插入新元素，可能导致AVL的约束被破坏，所以需要进行单旋转或双旋转来满足AVL的限制
 * <p>
 * Created by gcdd1993 on 2021/3/15.
 */
public class AvlSearchTree<T extends Comparable<? super T>> {
    // 每个节点左右子树最大深度差，也就是每个节点高度差
    private static final int ALLOW_IMBALANCE = 1;

    private AvlNode<T> root;

    public AvlSearchTree() {
        root = null;
    }

    public void makeEmpty() {
        root = null;
    }

    public boolean isEmpty() {
        return root == null;
    }

    /**
     * 是否存在此元素
     *
     * @param t 元素
     * @return if find
     */
    public boolean contains(T t) {
        return contains(t, root);
    }

    public T findMin() {
        if (isEmpty()) return null;
        return findMin(root).element;
    }

    public T findMax() {
        if (isEmpty()) return null;
        return findMax(root).element;
    }

    // 需要处理重复元
    public void insert(T x) {
        root = insert(x, root);
    }

    // 删除通常来说，是最困难的
    public void remove(T x) {
        root = remove(x, root);
    }

    public void printTree() {

    }

    /**
     * 在子树中查找元素X
     *
     * @param x 要查找的元素X
     * @param t 子树的根节点
     * @return 如果能找到
     */
    private boolean contains(T x, AvlNode<T> t) {
        // 如果t是空集，表明已经查找到叶子节点，仍未找到，返回false
        if (t == null) {
            return false;
        }
        // 直接与子树的根节点比较，由于是二叉查找树，与根节点比较，将得出在左子树还是右子树。
        int compareResult = x.compareTo(t.element);
        // 比根节点小，说明在左子树
        if (compareResult < 0) {
            // 递归操作，空间复杂度为O(logN)
            return contains(x, t.left);
        } else
            // 比根节点大，说明在右子树
            if (compareResult > 0) {
                return contains(x, t.right);
            } else {
                // 与根节点相同，说明已经找到
                return true;
            }
    }

    /**
     * 从根节点开始，只要有左子树，就向下遍历，终止点就是最小的元素
     * <p>
     * 递归方式
     *
     * @param t 子树根节点
     * @return min element
     */
    private AvlNode<T> findMin(AvlNode<T> t) {
        if (t == null) {
            return null;
        } else if (t.left == null) {
            return t;
        }
        return findMin(t.left);
    }

    /**
     * 从根节点开始，只要有右子树，就向下遍历，终止点就是最大的元素
     * <p>
     * 非递归
     *
     * @param t 子树根节点
     * @return max element
     */
    private AvlNode<T> findMax(AvlNode<T> t) {
        if (t != null) {
            while (t.right != null) {
                t = t.right;
            }
        }
        return t;
    }

    /**
     * 插入新的元素到子树
     *
     * @param x 元素
     * @param t 元素所处的节点
     * @return the new root of the subtree
     */
    private AvlNode<T> insert(T x, AvlNode<T> t) {
        if (t == null) {
            return new AvlNode<>(x);
        }
        // 直接与子树的根节点比较，由于是二叉查找树，与根节点比较，将得出在左子树还是右子树。
        int compareResult = x.compareTo(t.element);
        if (compareResult < 0) {
            t.left = insert(x, t.left);
        } else if (compareResult > 0) {
            t.right = insert(x, t.right);
        } else {
            // 待插入的元素与现有的元素重复
        }
        // AVL 二叉树平衡
        return balance(t);
    }

    /**
     * 从子树中删除一个节点
     *
     * @param x 待删除的元素
     * @param t the node that roots the subtree.
     * @return the new root of the subtree
     */
    private AvlNode<T> remove(T x, AvlNode<T> t) {
        if (t == null) {
            return null;
        }
        // 直接与子树的根节点比较，由于是二叉查找树，与根节点比较，将得出在左子树还是右子树。
        int compareResult = x.compareTo(t.element);
        if (compareResult < 0) {
            t.left = remove(x, t.left);
        } else if (compareResult > 0) {
            t.right = remove(x, t.right);
        } else if (t.left != null && t.right != null) {
            // 用其右子树的最小的数据代替该节点的数据，并递归地删除那个节点(现在它是空的)
            t.element = findMin(t.right).element;
            t.right = remove(t.element, t.right);
        } else {
            t = (t.left != null) ? t.left : t.right;
        }
        // AVL 二叉树平衡
        return balance(t);
    }

    /**
     * 保证t插入后，avl tree 维持平衡
     *
     * @param t node
     * @return the new root of the subtree
     */
    private AvlNode<T> balance(AvlNode<T> t) {
        if (t == null) {
            return null;
        }
        if (height(t.left) - height(t.right) > ALLOW_IMBALANCE) {
            if (height(t.left.left) >= height(t.left.right)) {
                // 单旋转
                t = rotateWithLeftChild(t);
            } else {
                // 双旋转
                t = doubleWithLeftChild(t);
            }
        } else if (height(t.right) - height(t.left) > ALLOW_IMBALANCE) {
            if (height(t.right.right) >= height(t.right.left)) {
                t = rotateWithRightChild(t);
            } else {
                t = doubleWithLeftChild(t);
            }
        }
        t.height = Math.max(height(t.left), height(t.right)) + 1;
        return t;
    }

    /**
     * Rotate binary tree node with left child
     * For AVL trees, this is a single rotation for case 1.
     * Update heights, then return new root.
     *
     * <img src="data:image/png;base64,"/>
     */
    private AvlNode<T> rotateWithLeftChild(AvlNode<T> k2) {
        AvlNode<T> k1 = k2.left;
        k2.left = k1.right;
        k1.right = k2;
        k2.height = Math.max(height(k2.left), height(k2.right)) + 1;
        k1.height = Math.max(height(k1.left), k2.height) + 1;
        return k1;
    }

    private AvlNode<T> rotateWithRightChild(AvlNode<T> k2) {
        AvlNode<T> k1 = k2.right;
        k2.right = k1.left;
        k1.left = k2;
        k2.height = Math.max(height(k2.right), height(k2.left)) + 1;
        k1.height = Math.max(height(k1.right), k2.height) + 1;
        return k1;
    }

    /**
     * Double rotate binary tree node: first left child
     * with its right child; then node k3 with new left child.
     * For AVL trees, this is a double rotation for case 2.
     * Update heights, then return new root.
     *
     * <img src="data:image/png;base64,"/>
     */
    private AvlNode<T> doubleWithLeftChild(AvlNode<T> k3) {
        k3.left = rotateWithRightChild(k3.left);
        return rotateWithLeftChild(k3);
    }

    /**
     * 返回节点的高度（与根节点的路径差）
     */
    private int height(AvlNode<T> t) {
        return t == null ? -1 : t.height;
    }

    /**
     * 二叉查找树节点
     */
    private static class AvlNode<T> {

        // 需要实现Comparable接口
        private T element;
        private AvlNode<T> left;
        private AvlNode<T> right;
        // avl树的高度，因为实际上需要左右子树的深度小于等于1，只需要在插入时，保证插入节点的高度与整个树的高度差不大于1即可
        private int height;

        public AvlNode(T element, AvlNode<T> left, AvlNode<T> right) {
            this.element = element;
            this.left = left;
            this.right = right;
        }

        public AvlNode(T element) {
            this.element = element;
        }

        public Object getElement() {
            return element;
        }

        public void setElement(T element) {
            this.element = element;
        }

        public AvlNode<T> getLeft() {
            return left;
        }

        public void setLeft(AvlNode<T> left) {
            this.left = left;
        }

        public AvlNode<T> getRight() {
            return right;
        }

        public void setRight(AvlNode<T> right) {
            this.right = right;
        }
    }
}
